mapper.select:
		select sp,MEM_USED,peservice_exe_vsz,peservice_exe_rss,csx_ic_safe_vsz,csx_ic_safe_rss,ECOM_vsz,ECOM_rss,
		       mozzo_sh_vsz,mozzo_sh_rss,tomcat_sh_vsz,tomcat_sh_rss,TLDlistener_exe_vsz,TLDlistener_exe_rss,poll_datetime
		from ${tableName}
		where array_name = #{arrayName}
		and version = #{smallVersion}

    	and poll_datetime >= #{beginTime}


    	and poll_datetime <= #{endTime}

		order by poll_datetime

select distinct version
from ${tableName}
where array_name = #{arrayName} order by version

		select sp,CPU_FILT,cpu_peservice_exe,cpu_csx_ic_safe,cpu_ECOM,cpu_mozzo_sh,cpu_tomcat_sh,cpu_TLDlistener_exe,poll_datetime
		from ${tableName}
		where array_name = #{arrayName}
		and version = #{smallVersion}

    	and poll_datetime >= #{beginTime}


    	and poll_datetime <= #{endTime}

		order by poll_datetime
